// ---- PARAMÈTRES HYSTÉRÉSIS ----
const seuil_on = 500;       // Surplus minimum pour activer (W)
const seuil_off = 300;      // Surplus maximum pour désactiver (W)
const delai_on = 30 * 1000; // Durée d'activation en ms
const delai_off = 5 * 1000;// Durée de désactivation en ms

// ---- RÉCUPÉRATION DES DONNÉES ----
let surplus = Number(msg.payload.puissance_injectee || 0);

// ---- CONTEXTE PERSISTANT ----
let etat_actuel = context.get("etat");
if (!etat_actuel) {
    etat_actuel = "OFF";
    context.set("etat", "OFF");
}

let timer_on = context.get("timer_on");
let timer_off = context.get("timer_off");

node.warn({
    etat_actuel: context.get("etat"),
    surplus: surplus,
    timer_on: timer_on ? "running" : null,
    timer_off: timer_off ? "running" : null
});

// ---- LOGIQUE ----

// 1️⃣ SI CHARGE ACTUELLEMENT OFF → Chercher un surplus stable pour l'allumer
if (etat_actuel === "OFF") {

    if (surplus > seuil_on) {
        if (!timer_on) {
            timer_on = setTimeout(() => {
                context.set("etat", "ON");
                node.warn("Envoi ON");
                node.send({ topic: "surplus", payload: "ON" });
            }, delai_on);
            context.set("timer_on", timer_on);
        }
    } else {
        // trop faible → annule le timer ON
        if (timer_on) {
            clearTimeout(timer_on);
            context.set("timer_on", null);
        }
    }
}

// 2️⃣ SI CHARGE ACTUELLEMENT ON → Vérifier si le surplus est trop faible pour couper
if (etat_actuel === "ON") {

    if (surplus < seuil_off) {
        if (!timer_off) {
            timer_off = setTimeout(() => {
                context.set("etat", "OFF");
                node.warn("Envoi OFF");
                node.send({ topic: "surplus", payload: "OFF" });
            }, delai_off);
            context.set("timer_off", timer_off);
        }
    } else {
        // surplus OK → annule timer OFF
        if (timer_off) {
            clearTimeout(timer_off);
            context.set("timer_off", null);
        }
    }
}
node.warn({
    etat_actuel,
    surplus,
    timer_on: timer_on ? "actif" : "null",
    timer_off: timer_off ? "actif" : "null"
});

